import { makeStyles } from '@material-ui/core/styles';

export default makeStyles({
  TodoForm: {
    width: '100%',
    marginBottom: '1rem'
  },
  input: {
    width: '100%',
    height: '2rem',
    background: 'none',
    border: 'none',
    borderBottom: '1px solid #34495e',
    color: '#34495e',
    fontSize: '1.2rem',
    fontWeight: '300',
    outline: 'none'
  }
});
